/// <summary>
/// Gets or sets the starting date for the report period
/// </summary>
[
    DataMemberAttribute('FromDate'),
    SysOperationLabelAttribute(literalstr("@SYS5209")),
    SysOperationHelpTextAttribute(literalstr("@SYS5209")),
    SysOperationGroupMemberAttribute('FromDate'),
    SysOperationDisplayOrderAttribute('1')
]
public TransDate parmFromDate(TransDate _fromDate = fromDate)
{
    fromDate = _fromDate;
    return fromDate;
}

/// <summary>
/// Gets or sets the ending date for the report period
/// </summary>
[
    DataMemberAttribute('ToDate'),
