public InventLocationId parmWarehouse(InventLocationId _inventLocationId = inventLocationId)
{
    inventLocationId = _inventLocationId;
    return inventLocationId;
}

/// <summary>
/// Validates the parameter values to ensure logical consistency
/// </summary>
/// <returns>True if validation passes, false if validation fails</returns>
/// <remarks>
/// Checks that:
/// - To date is greater than From date when both are provided
/// - Both dates are specified if either one is provided
/// </remarks>
public boolean  validate()
{
    boolean ok = true;
    if(fromdate &&  Todate)
    {
        if(fromdate >   Todate)
        {
            ok = checkFailed("To date must be greater from date.");
        }
    }
    if(fromdate)
    {
        if(!Todate)
        {
            ok = checkFailed("To date must be specified.");
        }
    }
    if(Todate)
    {
        if(!fromdate)
        {
            ok = checkFailed("From date must be specified.");
        }
    }
    return ok;
}
