Method.Source:
/// <summary>
/// Sets the background color for a goal record in the form based on its status.
/// </summary>
/// <param name="_record">
/// The current record from the form data source. Expected to be of type HcmGoal.
/// </param>
/// <param name="_options">
/// The FormRowDisplayOption object that controls the visual presentation of the row.
/// </param>
public void displayOption(Common _record, FormRowDisplayOption _options)
{
    // Cast the generic Common record to the specific HcmGoal table.
    HcmGoal HcmGoal = _record as HcmGoal;

    // Evaluate the status of the goal and apply the corresponding color.
    switch (HcmGoal.Status)
    {
        // Cases for positive progress
    case HRMGoalStatus::Completed:
    case HRMGoalStatus::OnTrack:
        // Apply a Light Green color for completed or on-track goals.
        _options.backColor(WinAPI::RGB2int(144, 238, 144));
        break;

        // Case for goals requiring attention
    case HRMGoalStatus::NeedsImprovement:
        // Apply a Light Orange color for goals that need improvement.
        _options.backColor(WinAPI::RGB2int(255, 165, 0));
        break;

        // Case for goals not yet initiated
    case HRMGoalStatus::NotStarted:
        // Apply a Light Yellow color for goals that have not been started.
        _options.backColor(WinAPI::RGB2int(255, 255, 224));
        break;
    }

    // Call the next method in the chain to ensure base behavior and other extensions run correctly.
    next displayOption(_record, _options);
}
