// Single vendor KPI
public VendKpiContract getVendorKpi(VendAccount _vendAccount)
{
    return VendKpiService::buildVendorKpi(_vendAccount);
}

// Batch KPI for multiple vendors
public List getVendorKpiBatch(List _vendAccounts)
{
    return VendKpiService::buildVendorKpiBatch(_vendAccounts);
}
