/// <summary>
/// Determines if concurrent deletes should throw exception.
/// </summary>
/// <returns>true, if exception should be thrown; otherwise, false.</returns>
public boolean shouldThrowExceptionOnZeroDelete()
{
    return true;
}

/// <summary>
///
/// </summary>
public static SalesAIAssistantParameters find(boolean _forUpdate = false)
{
    SalesAIAssistantParameters parameters;

    parameters.selectForUpdate(_forUpdate);
    select firstonly parameters;

    if (!parameters && !_forUpdate)
    {
        ttsbegin;
        parameters.APIEndpoint    = "http://localhost:8000";
        parameters.TimeoutSeconds = 300;
        parameters.IsEnabled      = IsEnabled::Yes;
        parameters.insert();
        ttscommit;

        select firstonly parameters;
    }

    return parameters;
}
