/// <summary>
/// Returns the single parameters record, creating it if it doesn't exist.
/// Standard D365 parameters table pattern.
/// </summary>
/// <param name="_forUpdate">Set true if record will be modified.</param>
/// <returns>SalesIntelligenceParameters record.</returns>
public static SalesIntelligenceParameters find(boolean _forUpdate = false)
{
    SalesIntelligenceParameters parameters;

    if (_forUpdate)
    {
        parameters.selectForUpdate(_forUpdate);
    }

    select firstonly parameters;

    if (!parameters)
    {
        parameters.PythonServerUrl  = "http://localhost:8000";
        parameters.TimeoutSeconds   = 300;
        parameters.IsEnabled        = IsEnabled::Yes;
        parameters.insert();
    }

    return parameters;
}
